function updateJournalEntry(updatedKey, updatedValue) {
  try {
    // Step 1: Log key and value for debugging
    console.log("Updated Key:", updatedKey);
    console.log("Updated Value:", updatedValue);

    // Step 2: Retrieve the current ListViewUpdatedValue
    let tempData = JSON.parse(JSON.stringify(ListViewUpdatedValue.value || {}));
    console.log("Current ListViewUpdatedValue:", tempData);

    // Step 3: Access the journal_entries array
    let journalEntries = tempData.journal_entries || [];
    console.log("Current journal_entries:", journalEntries);

    // Step 4: Determine the index of the row being updated
    const i = table5.selectedRowIndex; // Index of the selected row
    if (i < 0 || i >= journalEntries.length) {
      utils.showNotification({ title: "Error", message: "Invalid row index.", type: "error" });
      console.error("Error: Invalid row index:", i);
      return;
    }

    // Step 5: Update the specific key in the selected journal entry
    journalEntries[i] = {
      ...journalEntries[i], // Preserve other fields
      [updatedKey]: updatedValue // Update only the specified key
    };

    // Step 6: Save back to ListViewUpdatedValue
    tempData.journal_entries = journalEntries;
    ListViewUpdatedValue.setValue(tempData);

    // Step 7: Log the updated object
    console.log("Updated ListViewUpdatedValue:", tempData);

    // Success Notification
    utils.showNotification({ title: "Success", message: "Value updated successfully!", type: "success" });
  } catch (error) {
    console.error("An unexpected error occurred:", error);
    utils.showNotification({ title: "Error", message: "Unexpected error occurred.", type: "error" });
  }
}
